import React, {Component} from 'react'
import './App.css'

class App extends Component{
  data = []

  area = {
    width: "500px",
    height: "500px",
    border: "1px solid blue"
  }

  constructor(props){
    super(props);
    this.state = {
      list: this.data
    }
    this.doAction = this.doAction.bind(this)
  }

  doAction(e){
    let x = e.pageX
    let y = e.pageY
    this.data.push({x:x, y:y})
    this.setState({
      list: this.data
    })
  }

  draw(d){
    let s ={
      position: "absolute",
      left: (d.x -25)
    }
  }

  render(){
    return(
      <div>
        <h1 className="bg-primary text-white display-4">React</h1>
        <div className="container">
          <p className="subtitle">Count number</p>
          {this.state.flg ?
            <div className="alert alert-primary text-right">
              <p className='h5'>count: {this.state.msg}</p>
            </div>
            :
            <div className='alert alert-warning text-left'>
              <p className='h5'>{this.state.msg}です</p>
            </div>
          }
          <div className='text-center'>
            <button className='btn btn-primary' onClick={this.doAction}>
              Click
            </button>
          </div>
        </div>
      </div>
    )
  }
}

export default App
