import React, {useEffect, useState} from 'react'
import './App.css'



function usePersist(ky, initVal){
  const key = "hooks:" + ky
  const value = () =>{
    try{
      const item = window.localStorage.getItem(key)
      return item ? JSON.perse(item) : initVal
    }catch (err){
      console.log(err)
      return initVal;
    }
  }

  const setValue = (val) =>{
    try{
      setSavedValue(val)
      window.localStorage.setItem(key, JSON.stringify(val))
    }catch (err){
      console.log(err)
    }
  }

  const [savedValue, setSavedValue] = useState(value)
  return [savedValue, setValue]
}


function PlainMessage(props){
  const [msg, setCalc] = useCalc()

  const onChange = (e) =>{
    setCalc(e.target.value)
  }

  return(
    <div className='p-3 h5'>
      <h5>{msg}</h5>
      <input type="number" onChange={onChange} className='form-control' />
    </div>
  )
}

function AlertMessage(props){
  const [msg, setCalc] = useCalc(0, total)

  const onChange = (e) =>{
    setCalc(e.target.value)
  }

  return(
    <div className='alert alert-primary h5 text-primary'>
      <h5>{msg}</h5>
      <input type="number" onChange={onChange} min="0" max="10000" className='form-control' />
    </div>
  )
}

function CardMessage(props){
  const [msg, setCalc] = useCalc(0, tax)

  const onChange = (e) =>{
    setCalc(e.target.value)
  }

  return(
    <div className='card p-3 h5 border-primary'>
      <h5>{msg}</h5>
      <input type="range" onChange={onChange} min="0" max="10000" step="100" className='form-control' />
    </div>
  )
}

function App(){
  return(
    <div>
      <h1 className='bg-primary text-white display-4'>React</h1>
      <div className='container'>
        <h4 className='my-3'>Hooks sample</h4>
        <PlainMessage />
        <AlertMessage />
        <CardMessage />
      </div>
    </div>
  )
}

export default App
