import React from 'react'
import {connect} from 'react-redux'
import {addTodo} from '../actions'

const doAction = () => {
  dispatch(addTodo(input.value))
  input.value = ''
}

let AddTodo = ({dispatch}) => {
  let input

  return (
    <div>
      <input ref={(node) => {
        input = node
      }} />
      <button onClick={doAction}>
        Add Todo
      </button>
    </div>
  )
}

AddTodo = connect()(AddTodo)

export default AddTodo
